"use strict";(self.webpackChunknote_book=self.webpackChunknote_book||[]).push([[51527],{10584:(n,e,t)=>{t.r(e),t.d(e,{assets:()=>s,contentTitle:()=>a,default:()=>u,frontMatter:()=>r,metadata:()=>i,toc:()=>c});const i=JSON.parse('{"id":"Frontend/DOM/\u5192\u6ce1\u6355\u83b7\u6d41-\u4e8b\u4ef6\u4e0e\u4e8b\u4ef6\u6e90\u5bf9\u8c61-\u4e8b\u4ef6\u59d4\u6258","title":"\u5192\u6ce1\u6355\u83b7\u6d41-\u4e8b\u4ef6\u4e0e\u4e8b\u4ef6\u6e90\u5bf9\u8c61-\u4e8b\u4ef6\u59d4\u6258","description":"","source":"@site/docs/Frontend/DOM/70.\u5192\u6ce1\u6355\u83b7\u6d41-\u4e8b\u4ef6\u4e0e\u4e8b\u4ef6\u6e90\u5bf9\u8c61-\u4e8b\u4ef6\u59d4\u6258.md","sourceDirName":"Frontend/DOM","slug":"/Frontend/DOM/\u5192\u6ce1\u6355\u83b7\u6d41-\u4e8b\u4ef6\u4e0e\u4e8b\u4ef6\u6e90\u5bf9\u8c61-\u4e8b\u4ef6\u59d4\u6258","permalink":"/docs-hub/Frontend/DOM/\u5192\u6ce1\u6355\u83b7\u6d41-\u4e8b\u4ef6\u4e0e\u4e8b\u4ef6\u6e90\u5bf9\u8c61-\u4e8b\u4ef6\u59d4\u6258","draft":false,"unlisted":false,"editUrl":"https://github.com/sumingcheng/docs-hub/edit/main/docs/Frontend/DOM/70.\u5192\u6ce1\u6355\u83b7\u6d41-\u4e8b\u4ef6\u4e0e\u4e8b\u4ef6\u6e90\u5bf9\u8c61-\u4e8b\u4ef6\u59d4\u6258.md","tags":[],"version":"current","lastUpdatedBy":"sumingcheng","lastUpdatedAt":1733909372000,"sidebarPosition":70,"frontMatter":{},"sidebar":"domSidebar","previous":{"title":"\u4e8b\u4ef6\u5904\u7406\u51fd\u6570","permalink":"/docs-hub/Frontend/DOM/\u4e8b\u4ef6\u5904\u7406\u51fd\u6570-\u5192\u6ce1\u6355\u83b7-\u963b\u6b62\u5192\u6ce1\u9ed8\u8ba4\u4e8b\u4ef6"},"next":{"title":"\u9f20\u6807\u884c\u4e3a-pageXY\u5c01\u88c5-\u62d6\u62fd\u51fd\u6570\u5c01\u88c5","permalink":"/docs-hub/Frontend/DOM/\u9f20\u6807\u884c\u4e3a-pageXY\u5c01\u88c5-\u62d6\u62fd\u51fd\u6570\u5c01\u88c5"}}');var o=t(74848),l=t(28453);const r={},a=void 0,s={},c=[];function d(n){const e={code:"code",pre:"pre",...(0,l.R)(),...n.components};return(0,o.jsx)(e.pre,{children:(0,o.jsx)(e.code,{className:"language-markdown:docs/Frontend/DOM/70.\u5192\u6ce1\u6355\u83b7\u6d41-\u4e8b\u4ef6\u4e0e\u4e8b\u4ef6\u6e90\u5bf9\u8c61-\u4e8b\u4ef6\u59d4\u6258.md",children:'# DOM\u4e8b\u4ef6\u6d41\u4e0e\u4e8b\u4ef6\u59d4\u6258\n\n## \u4e8b\u4ef6\u6d41\n\u4e8b\u4ef6\u6d41\u63cf\u8ff0\u4e86\u9875\u9762\u63a5\u6536\u4e8b\u4ef6\u7684\u987a\u5e8f\u3002\u4e3b\u8981\u6709\u4e24\u79cd\u4e8b\u4ef6\u6d41\u6a21\u578b\uff1a\n1. `IE`\u63d0\u51fa\u7684\u4e8b\u4ef6\u5192\u6ce1\u6d41`Event Bubbling`\n2. `Netscape`\u63d0\u51fa\u7684\u4e8b\u4ef6\u6355\u83b7\u6d41`Event Capturing`\n\n## \u4e8b\u4ef6\u6d41\u9636\u6bb5\nDOM\u4e8b\u4ef6\u6d41\u5206\u4e3a\u4e09\u4e2a\u9636\u6bb5\uff1a\n1. \u4e8b\u4ef6\u6355\u83b7\u9636\u6bb5\uff1a\u4e8b\u4ef6\u4ece\u6587\u6863\u7684\u6839\u8282\u70b9\u6d41\u5411\u76ee\u6807\u8282\u70b9\uff0c\u9014\u4e2d\u7ecf\u8fc7\u5404\u4e2a\u5c42\u6b21\u7684DOM\u8282\u70b9\uff0c\u5728\u5404\u8282\u70b9\u4e0a\u89e6\u53d1\u6355\u83b7\u4e8b\u4ef6\u3002\n2. \u76ee\u6807\u9636\u6bb5\uff1a\u4e8b\u4ef6\u5230\u8fbe\u76ee\u6807\u8282\u70b9\uff0c\u89e6\u53d1\u76ee\u6807\u8282\u70b9\u7684\u4e8b\u4ef6\u5904\u7406\u7a0b\u5e8f\u3002\u5982\u679c\u4e00\u4e2a\u8282\u70b9\u7ed1\u5b9a\u4e86\u591a\u4e2a\u4e8b\u4ef6\u5904\u7406\u7a0b\u5e8f\uff0c\u5219\u6309\u7167\u5b83\u4eec\u5b9a\u4e49\u7684\u5148\u540e\u987a\u5e8f\u4f9d\u6b21\u6267\u884c\u3002\n3. \u4e8b\u4ef6\u5192\u6ce1\u9636\u6bb5\uff1a\u4e8b\u4ef6\u4ece\u76ee\u6807\u8282\u70b9\u6d41\u5411\u6587\u6863\u7684\u6839\u8282\u70b9\uff0c\u9014\u4e2d\u7ecf\u8fc7\u5404\u4e2a\u5c42\u6b21\u7684DOM\u8282\u70b9\uff0c\u5728\u5404\u8282\u70b9\u4e0a\u89e6\u53d1\u5192\u6ce1\u4e8b\u4ef6\u3002\n\n## DOM\u4e8b\u4ef6\u6a21\u578b\n1. `DOM0`\u7ea7\u4e8b\u4ef6\u6a21\u578b\uff1a\u901a\u8fc7`on`\u524d\u7f00\u7684\u5c5e\u6027\u6ce8\u518c\u4e8b\u4ef6\u5904\u7406\u7a0b\u5e8f\uff0c\u5982`onclick`\u3001`onmouseover`\u7b49\u3002\n2. `DOM1`\u7ea7\u4e8b\u4ef6\u6a21\u578b\uff1a\u6ca1\u6709\u5b9a\u4e49\u4e8b\u4ef6\u76f8\u5173\u7684\u5185\u5bb9\u3002\n3. `DOM2`\u7ea7\u4e8b\u4ef6\u6a21\u578b\uff1a\u5b9a\u4e49\u4e86`addEventListener()`\u548c`removeEventListener()`\u65b9\u6cd5\u6765\u6dfb\u52a0\u548c\u5220\u9664\u4e8b\u4ef6\u5904\u7406\u7a0b\u5e8f\u3002\u8fd9\u662fW3C\u7684\u6807\u51c6\u89c4\u8303\u3002\n\n## \u4e8b\u4ef6\u5bf9\u8c61\n\u5f53\u4e8b\u4ef6\u88ab\u89e6\u53d1\u65f6\uff0c\u6d4f\u89c8\u5668\u4f1a\u521b\u5efa\u4e00\u4e2a\u4e8b\u4ef6\u5bf9\u8c61\uff0c\u4f5c\u4e3a\u53c2\u6570\u4f20\u9012\u7ed9\u4e8b\u4ef6\u5904\u7406\u7a0b\u5e8f\uff0c\u5176\u4e2d\u5305\u542b\u4e86\u4e0e\u4e8b\u4ef6\u76f8\u5173\u7684\u8be6\u7ec6\u4fe1\u606f\u3002\n\n```javascript\n// \u6d4f\u89c8\u5668\u4f1a\u628a\u4e8b\u4ef6\u5bf9\u8c61\u5305\u88c5\u6210\u5f62\u53c2\u4f20\u9012\u8fc7\u6765\nwrapper.onclick = function (e) {\n  var e = e || window.event;\n  console.log(e);\n}\n```\n\n![](../../assets/c5db92a20ed70b4ab70688ae30e99874.png)\n\n## \u4e8b\u4ef6\u6e90\u5bf9\u8c61\n\u4e8b\u4ef6\u6e90\u5bf9\u8c61\u6307\u7684\u662f\u89e6\u53d1\u4e8b\u4ef6\u7684DOM\u5143\u7d20\u3002\u4e0d\u540c\u6d4f\u89c8\u5668\u5bf9\u4e8b\u4ef6\u6e90\u5bf9\u8c61\u7684\u5c5e\u6027\u652f\u6301\u6709\u6240\u4e0d\u540c\uff1a\n1. \u706b\u72d0\u53ea\u652f\u6301`target`\u5c5e\u6027\n2. `IE`\u53ea\u652f\u6301`srcElement`\u5c5e\u6027\n3. \u8c37\u6b4c\u4e24\u4e2a\u5c5e\u6027\u90fd\u652f\u6301\n\n![](../../assets/d7866e536156d864ff2261809af39c0b.png)\n\n## \u4e8b\u4ef6\u59d4\u6258\n\u4e8b\u4ef6\u59d4\u6258\u662f\u4e00\u79cd\u5229\u7528\u4e8b\u4ef6\u5192\u6ce1\u673a\u5236\u6765\u7b80\u5316\u4e8b\u4ef6\u5904\u7406\u7684\u6280\u672f\u3002\u5b83\u5c06\u4e8b\u4ef6\u5904\u7406\u7a0b\u5e8f\u6ce8\u518c\u5230\u4e00\u4e2a\u7236\u7ea7\u5143\u7d20\u4e0a\uff0c\u5f53\u5b50\u5143\u7d20\u89e6\u53d1\u4e8b\u4ef6\u65f6\uff0c\u4e8b\u4ef6\u4f1a\u5192\u6ce1\u5230\u7236\u7ea7\u5143\u7d20\uff0c\u4ece\u800c\u88ab\u7236\u7ea7\u5143\u7d20\u7684\u4e8b\u4ef6\u5904\u7406\u7a0b\u5e8f\u6355\u83b7\u5e76\u5904\u7406\u3002\u8fd9\u6837\u53ef\u4ee5\u907f\u514d\u7ed9\u6bcf\u4e2a\u5b50\u5143\u7d20\u90fd\u6ce8\u518c\u4e8b\u4ef6\u5904\u7406\u7a0b\u5e8f\uff0c\u63d0\u9ad8\u6027\u80fd\u3002\n\n\u4e0b\u9762\u662f\u4e00\u4e2a\u4e8b\u4ef6\u59d4\u6258\u7684\u793a\u4f8b\uff1a\n\n```html\n<!DOCTYPE html>\n<html lang="en">\n<head>\n  <meta charset="UTF-8">\n  <meta http-equiv="X-UA-Compatible" content="IE=edge">\n  <meta name="viewport" content="width=device-width, initial-scale=1.0">\n</head>\n<body>\n  <button>\u589e\u52a0li</button>\n  <ul>\n    <li>1</li>\n    <li>2</li>\n    <li>3</li>\n    <li>4</li>\n    <li>5</li>\n    <li>6</li>\n    <li>7</li>\n    <li>8</li>\n    <li>9</li>\n    <li>10</li>\n  </ul>\n  <script>\n    var list = document.getElementsByTagName(\'ul\')[0],\n      items = list.getElementsByTagName(\'li\'),\n      addBtn = document.getElementsByTagName(\'button\')[0];\n\n    list.onclick = function (e) {\n      var e = e || window.event,\n        target = e.target || e.srcElement;\n      console.log(target.innerText);\n    }\n\n    addBtn.onclick = function () {\n      var li = document.createElement(\'li\');\n      li.innerText = items.length + 1;\n      list.appendChild(li);\n    }\n  <\/script>\n</body>\n</html>\n```\n\n\u5728\u8fd9\u4e2a\u4f8b\u5b50\u4e2d\uff0c\u6211\u4eec\u7ed9`<ul>`\u5143\u7d20\u6ce8\u518c\u4e86\u4e00\u4e2a`onclick`\u4e8b\u4ef6\u5904\u7406\u7a0b\u5e8f\u3002\u5f53\u7528\u6237\u70b9\u51fb\u5217\u8868\u9879`<li>`\u65f6\uff0c\u70b9\u51fb\u4e8b\u4ef6\u4f1a\u5192\u6ce1\u5230`<ul>`\u5143\u7d20\u4e0a\uff0c\u88ab\u5176\u4e8b\u4ef6\u5904\u7406\u7a0b\u5e8f\u6355\u83b7\u3002\u901a\u8fc7`event.target`\u53ef\u4ee5\u83b7\u53d6\u5230\u5b9e\u9645\u88ab\u70b9\u51fb\u7684`<li>`\u5143\u7d20\u3002\u8fd9\u6837\u65e0\u8bba\u5217\u8868\u9879\u6709\u591a\u5c11\u4e2a\uff0c\u90fd\u53ea\u9700\u7ed9`<ul>`\u6ce8\u518c\u4e00\u4e2a\u4e8b\u4ef6\u5904\u7406\u7a0b\u5e8f\u5373\u53ef\u3002\n\n### \u83b7\u53d6\u5143\u7d20\u5217\u8868\u7684\u4e0b\u6807\n\u6709\u65f6\u6211\u4eec\u9700\u8981\u83b7\u53d6\u88ab\u70b9\u51fb\u7684\u5217\u8868\u9879\u5728\u5217\u8868\u4e2d\u7684\u4e0b\u6807\u4f4d\u7f6e\uff0c\u6709\u4e24\u79cd\u5e38\u89c1\u7684\u5b9e\u73b0\u65b9\u5f0f\uff1a\n\n#### \u65b9\u6cd51\uff1a\u5faa\u73af\u6bd4\u8f83\n\u901a\u8fc7\u5faa\u73af\u904d\u5386\u5217\u8868\uff0c\u6bd4\u8f83\u6bcf\u4e00\u9879\u4e0e\u88ab\u70b9\u51fb\u5143\u7d20\u662f\u5426\u76f8\u7b49\uff0c\u4ece\u800c\u5f97\u5230\u4e0b\u6807\u3002\n\n```javascript\nlist.onclick = function (e) {\n  var e = e || window.event,\n    target = e.target || e.srcElement;\n\n  for (var i = 0; i < items.length; i++) {\n    if (target === items[i]) {\n      console.log(i);\n      break;\n    }\n  }\n}\n```\n\n#### \u65b9\u6cd52\uff1a\u4f7f\u7528indexOf\n\u5c06`NodeList`\u8f6c\u6362\u4e3a\u6570\u7ec4\uff0c\u7136\u540e\u8c03\u7528\u6570\u7ec4\u7684`indexOf()`\u65b9\u6cd5\u67e5\u627e\u4e0b\u6807\u3002\n\n```javascript\nlist.onclick = function (e) {\n  var e = e || window.event,\n    target = e.target || e.srcElement;\n\n  var index = Array.prototype.indexOf.call(items, target);\n  console.log(index);\n}\n```\n\n\u4f7f\u7528`indexOf()`\u7684\u65b9\u5f0f\u66f4\u52a0\u7b80\u6d01\uff0c\u4f46\u662f\u8981\u6ce8\u610f`NodeList`\u5e76\u975e\u771f\u6b63\u7684\u6570\u7ec4\uff0c\u9700\u8981\u901a\u8fc7`call()`\u6765\u501f\u7528\u6570\u7ec4\u7684\u65b9\u6cd5\u3002\n\n'})})}function u(n={}){const{wrapper:e}={...(0,l.R)(),...n.components};return e?(0,o.jsx)(e,{...n,children:(0,o.jsx)(d,{...n})}):d(n)}},28453:(n,e,t)=>{t.d(e,{R:()=>r,x:()=>a});var i=t(96540);const o={},l=i.createContext(o);function r(n){const e=i.useContext(l);return i.useMemo((function(){return"function"==typeof n?n(e):{...e,...n}}),[e,n])}function a(n){let e;return e=n.disableParentContext?"function"==typeof n.components?n.components(o):n.components||o:r(n.components),i.createElement(l.Provider,{value:e},n.children)}}}]);