"use strict";(self.webpackChunknote_book=self.webpackChunknote_book||[]).push([["22799"],{95729:function(n,e,t){t.r(e),t.d(e,{default:()=>u,frontMatter:()=>r,metadata:()=>i,assets:()=>c,toc:()=>s,contentTitle:()=>a});var i=JSON.parse('{"id":"Frontend/DOM/\u5192\u6CE1\u6355\u83B7\u6D41-\u4E8B\u4EF6\u4E0E\u4E8B\u4EF6\u6E90\u5BF9\u8C61-\u4E8B\u4EF6\u59D4\u6258","title":"\u5192\u6CE1\u6355\u83B7\u6D41-\u4E8B\u4EF6\u4E0E\u4E8B\u4EF6\u6E90\u5BF9\u8C61-\u4E8B\u4EF6\u59D4\u6258","description":"","source":"@site/docs/Frontend/DOM/70.\u5192\u6CE1\u6355\u83B7\u6D41-\u4E8B\u4EF6\u4E0E\u4E8B\u4EF6\u6E90\u5BF9\u8C61-\u4E8B\u4EF6\u59D4\u6258.md","sourceDirName":"Frontend/DOM","slug":"/Frontend/DOM/\u5192\u6CE1\u6355\u83B7\u6D41-\u4E8B\u4EF6\u4E0E\u4E8B\u4EF6\u6E90\u5BF9\u8C61-\u4E8B\u4EF6\u59D4\u6258","permalink":"/docs-hub/Frontend/DOM/\u5192\u6CE1\u6355\u83B7\u6D41-\u4E8B\u4EF6\u4E0E\u4E8B\u4EF6\u6E90\u5BF9\u8C61-\u4E8B\u4EF6\u59D4\u6258","draft":false,"unlisted":false,"editUrl":"https://github.com/sumingcheng/docs-hub/edit/main/docs/Frontend/DOM/70.\u5192\u6CE1\u6355\u83B7\u6D41-\u4E8B\u4EF6\u4E0E\u4E8B\u4EF6\u6E90\u5BF9\u8C61-\u4E8B\u4EF6\u59D4\u6258.md","tags":[],"version":"current","lastUpdatedBy":"sumingcheng","lastUpdatedAt":1734249440000,"sidebarPosition":70,"frontMatter":{},"sidebar":"domSidebar","previous":{"title":"\u4E8B\u4EF6\u5904\u7406\u51FD\u6570","permalink":"/docs-hub/Frontend/DOM/\u4E8B\u4EF6\u5904\u7406\u51FD\u6570-\u5192\u6CE1\u6355\u83B7-\u963B\u6B62\u5192\u6CE1\u9ED8\u8BA4\u4E8B\u4EF6"},"next":{"title":"\u9F20\u6807\u884C\u4E3A-pageXY\u5C01\u88C5-\u62D6\u62FD\u51FD\u6570\u5C01\u88C5","permalink":"/docs-hub/Frontend/DOM/\u9F20\u6807\u884C\u4E3A-pageXY\u5C01\u88C5-\u62D6\u62FD\u51FD\u6570\u5C01\u88C5"}}'),o=t("85893"),l=t("50065");let r={},a=void 0,c={},s=[];function d(n){let e={code:"code",pre:"pre",...(0,l.a)(),...n.components};return(0,o.jsx)(e.pre,{children:(0,o.jsx)(e.code,{className:"language-markdown:docs/Frontend/DOM/70.\u5192\u6CE1\u6355\u83B7\u6D41-\u4E8B\u4EF6\u4E0E\u4E8B\u4EF6\u6E90\u5BF9\u8C61-\u4E8B\u4EF6\u59D4\u6258.md",children:'# DOM\u4E8B\u4EF6\u6D41\u4E0E\u4E8B\u4EF6\u59D4\u6258\n\n## \u4E8B\u4EF6\u6D41\n\u4E8B\u4EF6\u6D41\u63CF\u8FF0\u4E86\u9875\u9762\u63A5\u6536\u4E8B\u4EF6\u7684\u987A\u5E8F\u3002\u4E3B\u8981\u6709\u4E24\u79CD\u4E8B\u4EF6\u6D41\u6A21\u578B\uFF1A\n1. `IE`\u63D0\u51FA\u7684\u4E8B\u4EF6\u5192\u6CE1\u6D41`Event Bubbling`\n2. `Netscape`\u63D0\u51FA\u7684\u4E8B\u4EF6\u6355\u83B7\u6D41`Event Capturing`\n\n## \u4E8B\u4EF6\u6D41\u9636\u6BB5\nDOM\u4E8B\u4EF6\u6D41\u5206\u4E3A\u4E09\u4E2A\u9636\u6BB5\uFF1A\n1. \u4E8B\u4EF6\u6355\u83B7\u9636\u6BB5\uFF1A\u4E8B\u4EF6\u4ECE\u6587\u6863\u7684\u6839\u8282\u70B9\u6D41\u5411\u76EE\u6807\u8282\u70B9\uFF0C\u9014\u4E2D\u7ECF\u8FC7\u5404\u4E2A\u5C42\u6B21\u7684DOM\u8282\u70B9\uFF0C\u5728\u5404\u8282\u70B9\u4E0A\u89E6\u53D1\u6355\u83B7\u4E8B\u4EF6\u3002\n2. \u76EE\u6807\u9636\u6BB5\uFF1A\u4E8B\u4EF6\u5230\u8FBE\u76EE\u6807\u8282\u70B9\uFF0C\u89E6\u53D1\u76EE\u6807\u8282\u70B9\u7684\u4E8B\u4EF6\u5904\u7406\u7A0B\u5E8F\u3002\u5982\u679C\u4E00\u4E2A\u8282\u70B9\u7ED1\u5B9A\u4E86\u591A\u4E2A\u4E8B\u4EF6\u5904\u7406\u7A0B\u5E8F\uFF0C\u5219\u6309\u7167\u5B83\u4EEC\u5B9A\u4E49\u7684\u5148\u540E\u987A\u5E8F\u4F9D\u6B21\u6267\u884C\u3002\n3. \u4E8B\u4EF6\u5192\u6CE1\u9636\u6BB5\uFF1A\u4E8B\u4EF6\u4ECE\u76EE\u6807\u8282\u70B9\u6D41\u5411\u6587\u6863\u7684\u6839\u8282\u70B9\uFF0C\u9014\u4E2D\u7ECF\u8FC7\u5404\u4E2A\u5C42\u6B21\u7684DOM\u8282\u70B9\uFF0C\u5728\u5404\u8282\u70B9\u4E0A\u89E6\u53D1\u5192\u6CE1\u4E8B\u4EF6\u3002\n\n## DOM\u4E8B\u4EF6\u6A21\u578B\n1. `DOM0`\u7EA7\u4E8B\u4EF6\u6A21\u578B\uFF1A\u901A\u8FC7`on`\u524D\u7F00\u7684\u5C5E\u6027\u6CE8\u518C\u4E8B\u4EF6\u5904\u7406\u7A0B\u5E8F\uFF0C\u5982`onclick`\u3001`onmouseover`\u7B49\u3002\n2. `DOM1`\u7EA7\u4E8B\u4EF6\u6A21\u578B\uFF1A\u6CA1\u6709\u5B9A\u4E49\u4E8B\u4EF6\u76F8\u5173\u7684\u5185\u5BB9\u3002\n3. `DOM2`\u7EA7\u4E8B\u4EF6\u6A21\u578B\uFF1A\u5B9A\u4E49\u4E86`addEventListener()`\u548C`removeEventListener()`\u65B9\u6CD5\u6765\u6DFB\u52A0\u548C\u5220\u9664\u4E8B\u4EF6\u5904\u7406\u7A0B\u5E8F\u3002\u8FD9\u662FW3C\u7684\u6807\u51C6\u89C4\u8303\u3002\n\n## \u4E8B\u4EF6\u5BF9\u8C61\n\u5F53\u4E8B\u4EF6\u88AB\u89E6\u53D1\u65F6\uFF0C\u6D4F\u89C8\u5668\u4F1A\u521B\u5EFA\u4E00\u4E2A\u4E8B\u4EF6\u5BF9\u8C61\uFF0C\u4F5C\u4E3A\u53C2\u6570\u4F20\u9012\u7ED9\u4E8B\u4EF6\u5904\u7406\u7A0B\u5E8F\uFF0C\u5176\u4E2D\u5305\u542B\u4E86\u4E0E\u4E8B\u4EF6\u76F8\u5173\u7684\u8BE6\u7EC6\u4FE1\u606F\u3002\n\n```javascript\n// \u6D4F\u89C8\u5668\u4F1A\u628A\u4E8B\u4EF6\u5BF9\u8C61\u5305\u88C5\u6210\u5F62\u53C2\u4F20\u9012\u8FC7\u6765\nwrapper.onclick = function (e) {\n  var e = e || window.event;\n  console.log(e);\n}\n```\n\n![](../../assets/c5db92a20ed70b4ab70688ae30e99874.png)\n\n## \u4E8B\u4EF6\u6E90\u5BF9\u8C61\n\u4E8B\u4EF6\u6E90\u5BF9\u8C61\u6307\u7684\u662F\u89E6\u53D1\u4E8B\u4EF6\u7684DOM\u5143\u7D20\u3002\u4E0D\u540C\u6D4F\u89C8\u5668\u5BF9\u4E8B\u4EF6\u6E90\u5BF9\u8C61\u7684\u5C5E\u6027\u652F\u6301\u6709\u6240\u4E0D\u540C\uFF1A\n1. \u706B\u72D0\u53EA\u652F\u6301`target`\u5C5E\u6027\n2. `IE`\u53EA\u652F\u6301`srcElement`\u5C5E\u6027\n3. \u8C37\u6B4C\u4E24\u4E2A\u5C5E\u6027\u90FD\u652F\u6301\n\n![](../../assets/d7866e536156d864ff2261809af39c0b.png)\n\n## \u4E8B\u4EF6\u59D4\u6258\n\u4E8B\u4EF6\u59D4\u6258\u662F\u4E00\u79CD\u5229\u7528\u4E8B\u4EF6\u5192\u6CE1\u673A\u5236\u6765\u7B80\u5316\u4E8B\u4EF6\u5904\u7406\u7684\u6280\u672F\u3002\u5B83\u5C06\u4E8B\u4EF6\u5904\u7406\u7A0B\u5E8F\u6CE8\u518C\u5230\u4E00\u4E2A\u7236\u7EA7\u5143\u7D20\u4E0A\uFF0C\u5F53\u5B50\u5143\u7D20\u89E6\u53D1\u4E8B\u4EF6\u65F6\uFF0C\u4E8B\u4EF6\u4F1A\u5192\u6CE1\u5230\u7236\u7EA7\u5143\u7D20\uFF0C\u4ECE\u800C\u88AB\u7236\u7EA7\u5143\u7D20\u7684\u4E8B\u4EF6\u5904\u7406\u7A0B\u5E8F\u6355\u83B7\u5E76\u5904\u7406\u3002\u8FD9\u6837\u53EF\u4EE5\u907F\u514D\u7ED9\u6BCF\u4E2A\u5B50\u5143\u7D20\u90FD\u6CE8\u518C\u4E8B\u4EF6\u5904\u7406\u7A0B\u5E8F\uFF0C\u63D0\u9AD8\u6027\u80FD\u3002\n\n\u4E0B\u9762\u662F\u4E00\u4E2A\u4E8B\u4EF6\u59D4\u6258\u7684\u793A\u4F8B\uFF1A\n\n```html\n<!DOCTYPE html>\n<html lang="en">\n<head>\n  <meta charset="UTF-8">\n  <meta http-equiv="X-UA-Compatible" content="IE=edge">\n  <meta name="viewport" content="width=device-width, initial-scale=1.0">\n</head>\n<body>\n  <button>\u589E\u52A0li</button>\n  <ul>\n    <li>1</li>\n    <li>2</li>\n    <li>3</li>\n    <li>4</li>\n    <li>5</li>\n    <li>6</li>\n    <li>7</li>\n    <li>8</li>\n    <li>9</li>\n    <li>10</li>\n  </ul>\n  <script>\n    var list = document.getElementsByTagName(\'ul\')[0],\n      items = list.getElementsByTagName(\'li\'),\n      addBtn = document.getElementsByTagName(\'button\')[0];\n\n    list.onclick = function (e) {\n      var e = e || window.event,\n        target = e.target || e.srcElement;\n      console.log(target.innerText);\n    }\n\n    addBtn.onclick = function () {\n      var li = document.createElement(\'li\');\n      li.innerText = items.length + 1;\n      list.appendChild(li);\n    }\n  <\/script>\n</body>\n</html>\n```\n\n\u5728\u8FD9\u4E2A\u4F8B\u5B50\u4E2D\uFF0C\u6211\u4EEC\u7ED9`<ul>`\u5143\u7D20\u6CE8\u518C\u4E86\u4E00\u4E2A`onclick`\u4E8B\u4EF6\u5904\u7406\u7A0B\u5E8F\u3002\u5F53\u7528\u6237\u70B9\u51FB\u5217\u8868\u9879`<li>`\u65F6\uFF0C\u70B9\u51FB\u4E8B\u4EF6\u4F1A\u5192\u6CE1\u5230`<ul>`\u5143\u7D20\u4E0A\uFF0C\u88AB\u5176\u4E8B\u4EF6\u5904\u7406\u7A0B\u5E8F\u6355\u83B7\u3002\u901A\u8FC7`event.target`\u53EF\u4EE5\u83B7\u53D6\u5230\u5B9E\u9645\u88AB\u70B9\u51FB\u7684`<li>`\u5143\u7D20\u3002\u8FD9\u6837\u65E0\u8BBA\u5217\u8868\u9879\u6709\u591A\u5C11\u4E2A\uFF0C\u90FD\u53EA\u9700\u7ED9`<ul>`\u6CE8\u518C\u4E00\u4E2A\u4E8B\u4EF6\u5904\u7406\u7A0B\u5E8F\u5373\u53EF\u3002\n\n### \u83B7\u53D6\u5143\u7D20\u5217\u8868\u7684\u4E0B\u6807\n\u6709\u65F6\u6211\u4EEC\u9700\u8981\u83B7\u53D6\u88AB\u70B9\u51FB\u7684\u5217\u8868\u9879\u5728\u5217\u8868\u4E2D\u7684\u4E0B\u6807\u4F4D\u7F6E\uFF0C\u6709\u4E24\u79CD\u5E38\u89C1\u7684\u5B9E\u73B0\u65B9\u5F0F\uFF1A\n\n#### \u65B9\u6CD51\uFF1A\u5FAA\u73AF\u6BD4\u8F83\n\u901A\u8FC7\u5FAA\u73AF\u904D\u5386\u5217\u8868\uFF0C\u6BD4\u8F83\u6BCF\u4E00\u9879\u4E0E\u88AB\u70B9\u51FB\u5143\u7D20\u662F\u5426\u76F8\u7B49\uFF0C\u4ECE\u800C\u5F97\u5230\u4E0B\u6807\u3002\n\n```javascript\nlist.onclick = function (e) {\n  var e = e || window.event,\n    target = e.target || e.srcElement;\n\n  for (var i = 0; i < items.length; i++) {\n    if (target === items[i]) {\n      console.log(i);\n      break;\n    }\n  }\n}\n```\n\n#### \u65B9\u6CD52\uFF1A\u4F7F\u7528indexOf\n\u5C06`NodeList`\u8F6C\u6362\u4E3A\u6570\u7EC4\uFF0C\u7136\u540E\u8C03\u7528\u6570\u7EC4\u7684`indexOf()`\u65B9\u6CD5\u67E5\u627E\u4E0B\u6807\u3002\n\n```javascript\nlist.onclick = function (e) {\n  var e = e || window.event,\n    target = e.target || e.srcElement;\n\n  var index = Array.prototype.indexOf.call(items, target);\n  console.log(index);\n}\n```\n\n\u4F7F\u7528`indexOf()`\u7684\u65B9\u5F0F\u66F4\u52A0\u7B80\u6D01\uFF0C\u4F46\u662F\u8981\u6CE8\u610F`NodeList`\u5E76\u975E\u771F\u6B63\u7684\u6570\u7EC4\uFF0C\u9700\u8981\u901A\u8FC7`call()`\u6765\u501F\u7528\u6570\u7EC4\u7684\u65B9\u6CD5\u3002\n\n'})})}function u(n={}){let{wrapper:e}={...(0,l.a)(),...n.components};return e?(0,o.jsx)(e,{...n,children:(0,o.jsx)(d,{...n})}):d(n)}},50065:function(n,e,t){t.d(e,{Z:function(){return a},a:function(){return r}});var i=t(67294);let o={},l=i.createContext(o);function r(n){let e=i.useContext(l);return i.useMemo(function(){return"function"==typeof n?n(e):{...e,...n}},[e,n])}function a(n){let e;return e=n.disableParentContext?"function"==typeof n.components?n.components(o):n.components||o:r(n.components),i.createElement(l.Provider,{value:e},n.children)}}}]);